mapper.sql:
where 1=1


        and id = #{id}


        and tenant_id = #{tenantId}


        and project_id = #{projectId}


        and crowd_id = #{crowdId}


        and run_date = #{runDate}


        and device_attr_type = #{deviceAttrType}


        and device_attr_name = #{deviceAttrName}


        and metric_value = #{metricValue}


        and cycle_statistics = #{cycleStatistics}


        and start_date = #{startDate}


        and end_date = #{endDate}

mapper.select:
select

from TD_TENANT_DEVICE_COUNT
WHERE device_attr_name != '-1'

    and tenant_id = #{tenantId}


    and project_id = #{projectId}


    and crowd_id = #{crowdId}


    and cycle_statistics = #{cycleStatistics}


    and start_date = #{startDate}


    and end_date = #{endDate}


    and run_date = #{runDate}


    and device_attr_type=#{deviceAttrType}


    and device_attr_name in

        #{item}

select

from TD_TENANT_DEVICE_COUNT
WHERE 1=1

    and tenant_id = #{tenantId}


    and project_id = #{projectId}


    and crowd_id = #{crowdId}


    and cycle_statistics = #{cycleStatistics}


    and device_attr_type=#{deviceAttrType}


     AND run_date <= #{runDate}


    and device_attr_name in

        #{item}


order by run_date desc
limit 1;

mapper.delete:
delete from TD_TENANT_DEVICE_COUNT where tenant_id=#{tenantId} and project_id=#{projectId} and
crowd_id=#{crowdId} and run_date=#{runDate} and cycle_statistics=#{cycleStatistics} and start_date=#{startDate}
and end_date=#{endDate}
