mapper.update:
        UPDATE TD_METRIC_DAY t,TD_METRIC_DAY_PARTY p 
		SET t.member_count = p.member_count,t.potential_count = p.potential_count 
		WHERE 1=1 
		AND p.date = #{runDate}
		AND t.date = #{runDate}
		AND t.project_id = p.project_id AND t.date = p.date

mapper.select:
    select  
	from TD_METRIC_DAY where  date >= #{startDate} and date <= #{endDate}  
	group by project_id,tenant_id,YEAR(date),month,project_num

    select  
	from TD_METRIC_DAY where  date >= #{startDate} and date <= #{endDate}  
	group by project_id,tenant_id,YEAR(date),week_of_year,project_num
