mapper.sql:
where 1=1


    and id =  #{id}


    and tenant_id =  #{tenantId}


    and project_id =  #{projectId}


    and crowd_id =  #{crowdId}


    and run_date =  #{runDate}


    and tag_code =  #{tagCode}


    and tag_name =  #{tagName}


    and metric_value =  #{metricValue}


    and cycle_statistics =  #{cycleStatistics}


    and start_date =  #{startDate}


    and end_date =  #{endDate}

mapper.select:
    select  
	 from TD_TENANT_TAGS_COUNT 
	WHERE 1=1 

        and tenant_id =  #{tenantId}


        and project_id =  #{projectId}


        and crowd_id =  #{crowdId}


        and cycle_statistics =  #{cycleStatistics}


           AND run_date <= #{runDate} 


		and tag_code in

		        #{item}


	  order by run_date desc
	  limit 1;

    select  
	 from TD_TENANT_TAGS_COUNT 
	WHERE 1=1 

        and tenant_id =  #{tenantId}


        and project_id =  #{projectId}


        and crowd_id =  #{crowdId}


        and cycle_statistics =  #{cycleStatistics}


        and start_date =  #{startDate}


        and end_date =  #{endDate} 


        and run_date =  #{runDate} 


		and tag_code in

		        #{item}

    SELECT
    	IFNULL(sum(a.metric_value),0) AS metric_value
	FROM
	    TD_TENANT_TAGS_COUNT a
	left JOIN
	    TD_TAGS_INFO b
	ON
	    a.tag_code = b.tag_code
	WHERE 1=1 



                       and a.tag_code =  #{tagCode}


                      and a.tag_code in ('030101','030102')




        and tenant_id =  #{tenantId}


        and project_id =  #{projectId}


        and crowd_id =  #{crowdId}


        and cycle_statistics =  #{cycleStatistics}


        and run_date =  #{runDate}

SELECT a.tenant_id, a.project_id, a.crowd_id, a.tag_code, b.tag_name, a.metric_value, a.run_date, a.cycle_statistics, a.start_date, a.end_date FROM TD_TENANT_TAGS_COUNT a JOIN TD_TAGS_INFO b ON a.tag_code = b.tag_code WHERE 1=1 and a.tag_code LIKE '02%'
and tenant_id = #{tenantId}
and project_id = #{projectId}
and crowd_id = #{crowdId}
and cycle_statistics = #{cycleStatistics}
and run_date = #{runDate}

	select * from TD_TENANT_TAGS_COUNT t

		1=1 and t.tag_code LIKE '02%'

	        and tenant_id =  #{tenantId}


	        and project_id =  #{projectId}


	        and crowd_id =  #{crowdId}


            AND t.cycle_statistics = #{cycleStatistics}


        	 AND t.run_date <= #{runDate} 


	order by run_date desc limit 1;
